svg.script:
function random(min, max) { return Math.random() * (max - min) + min; }

// 文字库（可自定义，这里用季节相关词汇）
const words = ["春", "夏", "秋", "冬", "风", "花", "雪", "月", "雨", "露", "霜", "雾"];

function drawTextTree(x, y, length, angle, depth) {
  if (depth <= 0) return;

  const newX = x + Math.cos(angle) * length;
  const newY = y + Math.sin(angle) * length;

  // 随机线条样式
  const color = `hsl(${random(0, 360)}, ${random(40, 70)}%, ${random(30, 60)}%)`;
  const width = random(1, 3);

  // 绘制线条
  const line = document.createElementNS("http://www.w3.org/2000/svg", "line");
  line.setAttribute("x1", x);
  line.setAttribute("y1", y);
  line.setAttribute("x2", newX);
  line.setAttribute("y2", newY);
  line.setAttribute("stroke", color);
  line.setAttribute("stroke-width", width);
  document.querySelector("svg").appendChild(line);

  // 随机添加文字节点（深度越大，文字越小）
  const text = document.createElementNS("http://www.w3.org/2000/svg", "text");
  text.setAttribute("x", newX);
  text.setAttribute("y", newY);
  text.setAttribute("text-anchor", "middle");
  text.setAttribute("fill", color);
  text.setAttribute("font-size", 14 - depth * 1.5); // 深度增加，文字减小
  text.setAttribute("class", "text-node");
  text.textContent = words[Math.floor(random(0, words.length))];
  document.querySelector("svg").appendChild(text);

  // 递归绘制子树
  const branchAngle = random(0.2, 0.7);
  const newLength = length * random(0.6, 0.85);
  drawTextTree(newX, newY, newLength, angle - branchAngle, depth - 1);
  drawTextTree(newX, newY, newLength, angle + branchAngle, depth - 1);
}

// 从底部中间开始，深度7层
drawTextTree(400, 550, 90, -Math.PI/2, 7);
